import { Injectable } from '@angular/core';
import { cardTorneos } from 'src/app/interfaces/interface';
import { AngularFirestore } from '@angular/fire/firestore';

@Injectable({
  providedIn: 'root'
})
export class ServicesService {

  torneos : cardTorneos [] = [
    {
      nombreTorneo: 'El cabezon',
      img: 'data:image/jpeg;base64,',
      descripcionTorneo: 'lorem loremloremloremloremloremloremloremloremloremloremloremloremloremloremloremlorem ',
      participantesInscriptos: 2,
      precio : 150,
      premio : '$100',
      habilitado:true
    },
    {
      nombreTorneo: 'El traidor',
      img: 'data:image/jpeg;base64,',
      descripcionTorneo: 'lorem loremloremloremloremloremloremloremloremloremloremloremloremloremloremloremlorem ',
      precio : 150,
      premio : '$100',
      participantesInscriptos: 0,
      habilitado:false
    }
  ]

  constructor(private db : AngularFirestore) { }

  getTorneos(){
    return this.torneos;
  }

  
  getUsuarios(coleccion){
    return this.db.collection(coleccion).snapshotChanges();
  }

  
  
  insertUser(datos,coleccion){
    const param = JSON.parse(JSON.stringify(datos));
    return this.db.collection(coleccion).add(param);
  }
}
